import React, {ChangeEvent, KeyboardEvent, useState} from "react";
import {FilterValuesType} from "./App";

export type ToDoListPropsType = {
    title: string
    tasks: Array<TaskType>
    removeTask: (todoListId: string, taskId: string) => void
    changeFilter: (todoListId: string, value: FilterValuesType) => void
    todoListId: string
    addTask: (todoListId: string, title: string) => void
    filter: FilterValuesType
    changeTaskStatus: (todoListId: string, taskID: string, isDone: boolean) => void
}

export type TaskType = {
    id: string
    title: string
    isDone: boolean
}

export function ToDolis(props: ToDoListPropsType) {

    const [title, setTitle] = useState<string>('')
    const [error, setError] = useState<boolean>(false)

    // Методом .мар перебираем массив и засовываем каждый элемент в разметку
    const tasksJSXElements = props.tasks.map(elem => {
        const removeTask = () => props.removeTask(props.todoListId, elem.id);
        return (
            <li key={elem.id}>
                <input
                    type="checkbox"
                    checked={elem.isDone}
                    onChange={(event) => props.changeTaskStatus(props.todoListId, elem.id, event.currentTarget.checked)}
                />
                <span>{elem.title}</span>
                <button onClick={removeTask}>x</button>
            </li>)
    })

    const addTask = () => {
        const trimmedTitle = title.trim()
        if (trimmedTitle) {
            props.addTask(props.todoListId, trimmedTitle)
        } else {
            setError(true)
        }
        setTitle('')
    }

    const onAllClickHandler = () => props.changeFilter(props.todoListId, 'all')
    const onActiveClickHandler = () => props.changeFilter(props.todoListId, 'active')
    const onCompletedClickHandler = () => props.changeFilter(props.todoListId, 'completed')
    const onTitleChangeHandler = (event: ChangeEvent<HTMLInputElement>) => {
        setTitle(event.currentTarget.value)
        setError(false)
    }
    const onTitleKeyPressHandler = (event: KeyboardEvent<HTMLInputElement>) => {
        if (event.key === 'Enter') addTask()
    }

    const errorMessage = error
        ? <div className={'error-message'}> Title is required</div>
        : null

    return (
        <div>
            <h3>{props.title}</h3>
            <div>
                <input
                    className={error ? 'error' : ''}
                    value={title}
                    onChange={onTitleChangeHandler}
                    onKeyPress={onTitleKeyPressHandler}
                />
                <button onClick={addTask}>+</button>
                {errorMessage}
            </div>
            <ul>
                {/*Засунули елемент в разметку, который перебирали методом .мар*/}
                {tasksJSXElements}
            </ul>
            <div>
                <button
                    className={props.filter === 'all' ? 'active-filter' : ''}
                    onClick={onAllClickHandler}>All
                </button>
                <button
                    className={props.filter === 'active' ? 'active-filter' : ''}
                    onClick={onActiveClickHandler}>Active
                </button>
                <button
                    className={props.filter === 'completed' ? 'active-filter' : ''}
                    onClick={onCompletedClickHandler}>Completed
                </button>
            </div>
        </div>
    )
}
