import React, {ChangeEvent, useState} from "react";

type EditableInputType = {
    title: string
    changeTaskTitle: (title: string) => void
}

export function EditableInput(props: EditableInputType) {
    let [title, setTitle] = useState(props.title)
    let [editMode, setEditMode] = useState(false)

    function changeSetEditMode(e: ChangeEvent<HTMLInputElement>) {
        setTitle(e.currentTarget.value)
    }
    function onEditMode() {
        setEditMode(true)
    }
    function offEditMode() {
        setEditMode(false)
    }

    return (
        editMode
            ? <input autoFocus
                     value={title}
                     onChange={changeSetEditMode}
                     onBlur={offEditMode}/>
            : <span onDoubleClick={onEditMode}
            onClick={props.changeTaskTitle}>{props.title}</span>
    )
}
